INSERT INTO announcement (type, text) VALUES ("Cancelled train","The [time] hour service to [destination] has been cancelled because of [cancellation_reason]. Replacement bus services are free during rail disruption.");
INSERT INTO announcement (type, text) VALUES ("Cancelled train","The [time] hour service to [destination] will be delayed by [amount_of_time].");
INSERT INTO announcement (type, text) VALUES ("Cancelled train","The [time] hour service to [destination] will not stop at this station. Replacement bus services are free during rail disruption.");
INSERT INTO announcement (type, text) VALUES ("CCTV","24 hour CCTV recording is in operation at this station for your safety.");
INSERT INTO announcement (type, text) VALUES ("CCTV","May I have your attention please! Closed circuit televisions and remote video monitoring are active at this station.");
INSERT INTO announcement (type, text) VALUES ("Cycling","This is a safety announcement [safety_type]. Cycling or skateboarding within the station is not allowed.");
INSERT INTO announcement (type, text) VALUES ("Cycling","In the interest of safety, skate boarding and cycling at this station is strictly prohibited. Offenders may be prosecuted.");
INSERT INTO announcement (type, text) VALUES ("Fast Train","Stand well behind the platform edge. The next train at [platform_number] does not stop here. Stand behind the yellow line.");
INSERT INTO announcement (type, text) VALUES ("Fast Train","The train now approaching [platform_name] does not stop here. Please stand well clear of the edge of [platform_name].");
INSERT INTO announcement (type, text) VALUES ("Luggage","Please do not leave your luggage unattended at the station. Luggage left unattended may be removed and destroyed.");
INSERT INTO announcement (type, text) VALUES ("Luggage","A secure luggage safe keeping facility is located at [location]. Please do not leave your luggage unattended.");
INSERT INTO announcement (type, text) VALUES ("Next fastest service","Passengers for [destination], your next fastest service is now expected to be [amount_of_time] to [destination] departing from [platform_number]");
INSERT INTO announcement (type, text) VALUES ("Next train","The next train calling at [platform_number] is the [time] hour service to [destination].");
INSERT INTO announcement (type, text) VALUES ("Parking","Vehicles parked haphazardly will be clamped or towed. Short and long stay parking are available at this station.");
INSERT INTO announcement (type, text) VALUES ("Platform Alteration","This is a platform alteration! The [time] service to [destination] will now depart from [platform_number].");
INSERT INTO announcement (type, text) VALUES ("Platform Alteration","This is a platform change! The [time] service to [destination] will now leave from [platform_number].");
INSERT INTO announcement (type, text) VALUES ("Security","Security personnel patrol this station 24 hours a day.");
INSERT INTO announcement (type, text) VALUES ("BTP","This is a security message. If you see any suspicious looking person or article, please inform our staff, or press the emergency button.");
INSERT INTO announcement (type, text) VALUES ("Short Platforms","Due to short platforms, passengers for [destination] should board the [front_middle_rear] coach.");
INSERT INTO announcement (type, text) VALUES ("Short Platforms","Some stations have short platforms. If you are travelling to [destination], you will need to travel in the [front_middle_rear] carriages of the train.");
INSERT INTO announcement (type, text) VALUES ("Short-formed train","This train has [number_of_coach] coaches. Please accept our apologies for the reduced number of coaches today.");
INSERT INTO announcement (type, text) VALUES ("Smoking","Passengers are reminded that smoking is prohibited at all stations and on all train services.");
INSERT INTO announcement (type, text) VALUES ("Smoking","Please don't smoke or vape at this station.");
INSERT INTO announcement (type, text) VALUES ("Smoking","For your safety and comfort, this station operates a no smoking policy.");
INSERT INTO announcement (type, text) VALUES ("Smoking","All our trains and stations are no smoking areas. Please do not smoke until you have left the station premises.");
INSERT INTO announcement (type, text) VALUES ("Ticket Checks","Ticket checks are in operation at this station. Please have your ticket ready for inspection.");
INSERT INTO announcement (type, text) VALUES ("Tickets","Please buy a ticket before getting on to a train, otherwise you may have to pay a penalty fare.");
INSERT INTO announcement (type, text) VALUES ("Warm Weather","The weather forecast today is for temperatures to be high. Please ensure that you take note of the advice posters on display at all stations.");
INSERT INTO announcement (type, text) VALUES ("Wet Weather","This is a safety announcement [safety_type]. Due to today's wet weather, please take extra care whilst on the station.");
INSERT INTO announcement (type, text) VALUES ("Engineering Works","Due to essential engineering work, many trains are revised for the coming weekend and passengers are advised to check the revised times before travelling.");
INSERT INTO announcement (type, text) VALUES ("Fire Alarm Testing","Special announcement [safety_type] to all staff and passengers. As part of the weekly fire alarm test, you will hear an evacuation message. This is for testing purposes only.");
INSERT INTO announcement (type, text) VALUES ("Mind The Gap","Customers are reminded to mind the gap between the train and the platform edge when boarding.");
INSERT INTO announcement (type, text) VALUES ("Mind The Gap","Please mind the gap between the train and the platform edge.");
INSERT INTO announcement (type, text) VALUES ("Safety","Ladies and Gentlemen. For your own safety, please stand behind the yellow lines. When boarding, please mind the gap between the train and the platform. Thank you.");
INSERT INTO announcement (type, text) VALUES ("Door Locking","Please note, to ensure a timely departure, train doors may be locked shut from 30 to 40 seconds before departure");
INSERT INTO announcement (type, text) VALUES ("Door Locking","Please note, train doors will close in [amount_of_time] prior to departure.");
INSERT INTO announcement (type, text) VALUES ("Help Point Next Train Button","This station is [station]. The next train to arrive at [platform_name] in the direction of [destination] will be the [time] service to [destination].");
